import axios from "axios"
axios.defaults.baseURL = "http://localhost:3000/api"

export default{

    // 모든 글 불러오기
    getAllPosts() {
        return axios.get('/posts/')
    },

    // 글 작성하기
    createPost(data) {
        return axios.post('/posts/', data)
    },

}
